package pack1;

public class Swap2Nums {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=2, b=3;
		int temp;
		System.out.println("a before swap is "+a+" b before swap is "+b);
		temp=a;
		a=b;
		b=temp;
		System.out.println("a after swap is "+a+" b after swap is "+b);

	}

}
